import { connect } from 'react-redux';

const PressyButtonBase = ({ buttonPresses, buttonPressed }) => (
    <>
        <div>Welcome, welcome my friends! You've pressed the button {buttonPresses} times</div>
        <button onClick={buttonPressed}>Press meh</button>
    </>
)

const mapStateToProps = state => ({
    buttonPresses: state.buttonPresses,
})

const mapDispatchToProps = dispatch => ({
    buttonPressed: () => dispatch({ type: 'BUTTON_PRESSED' }),
});

export const PressyButton = connect(mapStateToProps, mapDispatchToProps)(PressyButton);
