export const buttonPresses = (state = { buttonPresses: 0 }, action) => {
    const { buttonPresses } = state;
    switch (action.type) {
        case 'BUTTON_PRESSED':
            return { ...state, buttonPresses: buttonPresses + 1 };
        default:
            return state;
    }
};
